import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import Loading from './loading'
import Gauge from './gauge'
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import './App.css';
import { withRouter } from 'react-router-dom'

const styles = theme => ({
  root: {
    ...theme.mixins.gutters(),
    paddingTop: theme.spacing.unit * 2,
    paddingBottom: theme.spacing.unit * 2,
  },
});



class Dashboard extends React.Component {

setgauge = (completes,courses) => {
  if ((completes/courses)*100 > this.state.gauge){
    console.log("yes")
  this.setState({ gauge: Math.trunc((completes/courses)*100) });
}}


  state = {
    gauge: 0
  };

  componentDidMount() {
    console.log("mounted")
    if (this.props.stateinfo.courses){
      console.log(this.props.stateinfo.courses)
    }
      }



render(){
  if (!this.props.stateinfo.user.completes){
  return (
    <div><Loading /></div>
  )}



  return (
    <div className="list" >

    {console.log(this.props)}

      <Paper props={this.props} className="paper" elevation={2}>
        <Typography props={this.props} variant="h1" component="h3">
        Welcome {this.props.stateinfo.user.firstname}
        </Typography>
      </Paper>
<div style={{flexDirection: 'row', display: 'flex', flexWrap:'wrap'}}>
<Paper props={this.props} className="paper" elevation={2} style={{marginTop: '15px', paddingTop: '10px', width:'33%', minWidth:'300px'}}>
<Typography variant="h2">
Current Lesson:
</Typography>
 <Paper props={this.props} onClick={() => this.props.history.push(`/courses/${this.props.nextCourse.id}`)} className="paper" elevation={5} style={{marginTop: '5px', marginBottom: '15px', paddingTop: '10px', width:'85%', backgroundColor:'#8AF0E8', opacity:'0.8'}}>

  <Typography variant="h3">
  {this.props.nextCourse && this.props.nextCourse.name}
  </Typography>
  </Paper>
</Paper>
      <Paper props={this.props} className="paper" elevation={2} style={{marginTop: '15px', paddingTop: '10px', width:'33%', minWidth:'300px'}}>
      {this.setgauge(this.props.stateinfo.user.completes.length,this.props.stateinfo.courses.length)}
      {this.props.stateinfo.courses && <Gauge value={Math.trunc((this.props.stateinfo.user.completes.length/this.props.stateinfo.courses.length)*100)}/>}
        <Typography variant="h5">
        {this.props.stateinfo.user.completes.length}/{this.props.stateinfo.courses.length} <br></br>
        Courses completed
        </Typography>
      </Paper>
      </div>
    </div>
  );
}
}

export default withRouter(withStyles(styles)(Dashboard));
