import React from 'react';
import PropTypes from 'prop-types';

import { withStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { connect } from 'react-redux';
import { fetchUser } from './fetchUser';
import { withRouter, Link } from 'react-router-dom'

const styles = theme => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
  },
  dense: {
    marginTop: 16,
  },
  menu: {
    width: 200,
  },
  button: {
    marginTop: 6,
    marginLeft: 18 ,
    opacity: 1,
    color: 'red',
    background: 'black',
  },
  button2:{
    marginTop: 6,
    marginLeft: 22 ,
    opacity: 1,
    color: 'red',
    background: 'black',
  },
  input: {
    display: 'none',
  },
  welcome:{
    marginLeft: -18,
  },

});


class Loginbox extends React.Component {
  state = {
    name: '',
    password: '',
  };

  handleChange = name => event => {
    this.setState({
      [name]: event.target.value,
    });
  };

routeHome = () => this.props.history.push('/home')

  render() {
    const { classes } = this.props;

    return (
      <div>
      <Typography className={classes.welcome} variant="h2" noWrap>
      Welcome
      </Typography>
      <form className={classes.container} noValidate autoComplete="off">
        <TextField
          id="outlined-name"
          label="Username"
          className={classes.textField}
          value={this.state.name}
          onChange={this.handleChange('name')}
          margin="normal"
          variant="outlined"
        />

        <TextField
          id="outlined-password"
          label="Password"
          type="password"
          value={this.state.password}
          className={classes.textField}
          onChange={this.handleChange('password')}
          margin="normal"
          variant="outlined"
        />
        <Button onClick={() => this.props.dispatchUser(this.state,this.routeHome)} variant="contained"  className={classes.button}>
        Login
      </Button>
      <Button onClick={this.routeHome} variant="contained"  className={classes.button2}>
        Signup
      </Button>

      </form>
      </div>
    );
  }
}

Loginbox.propTypes = {
  classes: PropTypes.object.isRequired,
};

const mapDispatchToProps = dispatch => {
  return {
    dispatchUser: (input, route) => {
      dispatch(fetchUser(input, route))
    }
  };
};

export default withRouter(connect(null, mapDispatchToProps)(withStyles(styles)(Loginbox)));
