import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import Loading from './loading'
import Completedswitch from './completedswitch'
import './App.css';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Switch from '@material-ui/core/Switch';

const styles = theme => ({
  root: {
    ...theme.mixins.gutters(),
    paddingTop: theme.spacing.unit * 2,
    paddingBottom: theme.spacing.unit * 2,
  },
});


setStart = () => {
  let temp = this.testfunction()
  this.setState({ completed: temp })}


class Course extends React.Component {

  testfunction = (course) => this.props.user.completes.find(function (complete) { return complete.course_id === course})?true:false

  state = {
    completed: false,
  };



  handleChange = name => event => {
    this.setState({ completed: !this.state.completed });
  };

render(){
  if (!this.props.course){
  return (
    <div><Loading /></div>
  )}



  return (
    <div className="list">
{console.log(this.props)}
{console.log(this.props.course.id)}
{console.log(this.props.user.completes.find(function (complete) { return complete.course_id === 1})?true:false)}
{console.log(this.testfunction())}
{this.setStart(this.props.course.id)}
      <Paper props={this.props} className="paper" elevation={2}>

        <Typography props={this.props} variant="h1" component="h3">
        {this.props.course.name}
        </Typography>
        <Typography style={{paddingTop: '8px'}} component="p">
        {this.props.course.description}
        </Typography>
      </Paper>
<Paper style={{marginTop: '15px', padding: '25px', maxWidth:'90%', minWidth:'300px'}}
props={this.props} className="paper" elevation={2}>
<Typography component="p">
{this.props.course.content}
</Typography>
</Paper>
<Paper style={{marginLeft: 'auto', marginRight:'auto', marginTop: '10px', maxWidth:'300px', minWidth:'100px'}}>

      <Switch
        checked={this.state.completed}
        onChange={this.handleChange('completed')}
        value="completed"
      />

</Paper>
    </div>
  );
}
}

export default withStyles(styles)(Course);
