export function nextCourse(thiscourse, thisuser, courses) {

let thiscourseobject = courses.find(function (course) {return course.id === thiscourse})

  function completeCheck(course,user) {
    return course.completes.find(function (complete) {return complete.user_id === user.id})? true:false
  }

  let output = completeCheck(thiscourse,thisuser)


return output

}
